export default HomeNavbar;

const Container = styled.section`
  --container_padding: 32px 5%;
  --nav_links_gap: 80px;
  --auth_links_gap: 80px;

  &&& {
    padding: var(--container_padding);
    position: relative;

    .navlinks {
      gap: var(--nav_links_gap);
      transition: 0.2s all ease-in-out;

      .links {
        cursor: pointer;
        white-space: nowrap;

        color: var(--Text---Body, #829095);
        font-family: "Open Sans";
        font-size: 24px;
        font-style: normal;
        font-weight: 600;
        line-height: 150%;

        &.active {
          color: var(--Text---Title, #122025);
        }
      }
    }

    #toggleMenu,
    label[for="toggleMenu"] {
      display: none;
    }

    .auth_links {
      gap: var(--auth_links_gap);

      .links {
        cursor: pointer;
        padding: 10px;
        border-radius: 10px;
        color: var(--Text---Title, #122025);
        font-family: "Open Sans";
        font-size: 28px;
        font-style: normal;
        font-weight: 600;
        line-height: 150%;

        &.blue {
          padding: 16px 40px;
          background: var(--Primary---500, #06aae4);

          color: var(--White, #fff);
          font-family: "Open Sans";
          font-size: 24px;
          font-style: normal;
          font-weight: 700;
          line-height: 150%;
        }
      }
    }

    @media screen and (max-width: 1300px) {
      --container_padding: 32px 4%;
      --nav_links_gap: 40px;
      --auth_links_gap: 40px;

      .navlinks {
        position: absolute;
        width: 100%;
        height: fit-content;
        display: flex;
        justify-content: space-around;
        height: 50px;

        inset: 0;
        top: 100%;
        scale: 0;
        transform: translate(0, -200px);
        background: #ffffff !important;
        box-shadow: 0 0 0 2px #00000010;
        padding: 10px 0;
      }

      label[for="toggleMenu"] {
        display: block;
      }

      #toggleMenu {
        display: none;

        &:checked ~ .navlinks {
          scale: 1;
          transform: translate(0, 0);
        }
      }
    }

    @media screen and (max-width: 900px) {
      .auth_links {
        padding: 5px 0 10px;
        box-shadow: 0 0 0 2px #00000010;

        position: absolute;
        width: 100%;
        display: flex;
        justify-content: space-around;
        transition: 0.3s all ease-in-out;

        inset: 0;
        top: calc(100% + 50px);
        height: fit-content;
        scale: 0;
        transform: translate(0, -200px);
        background: #ffffff;
      }

      #toggleMenu {
        box-shadow: 0 0 0 2px #00000010;
        display: none;

        &:checked ~ .navlinks {
          scale: 1;
          transform: translate(0, 0);
        }

        &:checked ~ .auth_links {
          scale: 1;
          transform: translate(0, 0);
        }
      }
    }
  }
`;
